const Timer = ({initialTime, propDone}) => {
  const [time, setTime] = useState(initialTime)
  const [isRunning, setIsRunning] = useState(false)
  const [done, setDone] = useState(propDone)

  const startTimer = () => {
    if (!isRunning) {
      let timer = setInterval(() => {
        if (time > 0) {
          setTime(() => time = time - 1);
        } else {
          clearInterval(timer);
          setIsRunning(false);
        }
      }, 1000);

      setIsRunning(true);
    }
  };

  const pauseTimer = () => {
    // clearInterval(timer);
    setIsRunning(false);
  };

  const minutes = Math.floor(time / 60);
  const seconds = time % 60;

  return (
    <>
      <button className="icon icon-play" onClick={startTimer} disabled={isRunning}></button>
      <button className="icon icon-pause" onClick={pauseTimer} disabled={!isRunning}></button>
      {` ${minutes}:${seconds}`}
    </>
  )
}

export default Timer
