import { Component } from 'react';
// import './new-task-form.css';

export default class NewTaskForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      label: '',
      min: '',
      sec: '',
    };
  }

  handleInputChange = (event) => {
    console.log('e.ta.name: ',event.target.name)
  }

  onLabelChange = (event) => {
    console.log('labelC: ',event.target.value)
    this.setState({
      label: event.target.value,
    });
  };

  onMinChange = (event) => {
    console.log('Min: ',event.target.value)
    this.setState({
      min: event.target.value,
    });
  };

  onSecChange = (event) => {
    console.log('Sec: ',event.target.value)
    this.setState({
      sec: event.target.value,
    });
  };

  onSubmit = (event) => {
