    //   sec: '',
    // });
  };

  render() {
    return (
      <header className="app-header">
        <h1 className="app-title">todos</h1>
        <form className="new-todo-form" onSubmit={(e) => console.log('submit')}>
          <input
            className="new-todo"
            placeholder="What needs to be done?"
            onChange={this.onLabelChange}
            value={this.state.label}
            autoFocus
          ></input>
          {/* Два новых ИНПУТА */}
          <input 
            className="new-todo-form__timer"
            placeholder="Min" 
            onChange={this.handleInputChange}
            value={this.state.min}
            name="minute"
            type="number"
            autoFocus
          ></input>
          <input 
            className="new-todo-form__timer"
            placeholder="Sec"
            onChange={this.handleInputChange}
            value={this.state.sec}
            name="second"
            type="number"
            autoFocus
          ></input>
        </form>
      </header>
    );
  }
}
