import { formatDistanceToNow } from 'date-fns';
import { Component } from 'react';
// import './task.css';

export default class Task extends Component {
  constructor() {
    super()
    this.state = {
      label: '',
      edit: false,
    }
  }

  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000)
    // this.timerID = setInterval( () => { console.log('timer',this.props.timer)}, 1000 )
    // this.timerID = setInterval(() => this.tick(), 1000)
    // this.countDownTimer = setInterval( () => this.tick(), 1000)
  }

  tick() {
    this.setState({})
  }

  countTimer(time) {
    if (time > 0) 
    console.log('this.props.timer: ', time = time - 1)
  }

  startTimer(time) {
    this.timerID = setInterval(() => this.countTimer(time), 1000);
  }

  onEdited() {
    this.setState({
      edit: true,
      label: this.props.label,
    });
  };

  changeLabel(event) {
    this.setState({
      label: event.target.value,
    });
  };

  onSubmitEdit(event) {
    event.preventDefault();
    const { updateEdit, id } = this.props;
    const text = this.state.label;
    updateEdit(id, text);
    this.setState({
      label: '',
      edit: false,
    });
  };

  render() {
    console.log('rener(): ')
    const { label, done, id, date, timer, onDeleted, onCompleted } = this.props;
    const min = Math.floor(timer / 60)
    const sec = timer % 60    
    let dateCreated = formatDistanceToNow(date, { includeSeconds: true, addSuffix: true });
    let liClass = '';
    if (done) {
      liClass += ' completed';
    }
    if (this.state.edit) liClass += ' editing';

    return (
      <li className={liClass}>
        <div className="view">
          <input
            id={id}
            className="toggle"
            type="checkbox"
            checked={done}
            onChange={(event) => {
              onCompleted(id, event.target.checked);
            }}
          ></input>
          <label htmlFor={id}>
            <span className="title">{label}</span>
            <span className="description">
                <button className="icon icon-play" onClick={() => { this.startTimer(this.props.timer) }}></button>
                <button className="icon icon-pause" onClick={() => { this.stopTimer()}}></button>
                {` ${min}:${sec}-`}
            </span>
            <span className="description">{dateCreated}</span>
          </label>
          <button className="icon icon-edit" onClick={this.onEdited}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
        <form onSubmit={this.onSubmitEdit}>
          <input
            className="edit"
            type="text"
            value={this.state.label}
            onChange={(event) => this.changeLabel(event)}
          ></input>
        </form>
      </li>
    );
  }
}
