  //   });
  // };

  // onMinChange = (event) => {
  //   console.log('Min: ',event.target.value)
  //   this.setState({
  //     min: event.target.value,
  //   });
  // };

  // onSecChange = (event) => {
  //   console.log('Sec: ',event.target.value)
  //   this.setState({
  //     sec: event.target.value,
  //   });
  // };

  onEnter = (event) => {
    if(event.key === 'Enter') {
      this.props.onItemAdded(this.state.label, this.state.min, this.state.sec)
      this.setState({
        label: '',
        min: '',
        sec: '',
      })
    }
  }

  //onSubmit = (event) => {
    // event.preventDefault();
    //console.log(event)
    // if (this.state.label.trim()) 
    //     this.props.onItemAdded(
    //     this.state.label, 
    //     this.state.min,
    //     this.state.sec);
    // else alert('O_0ps! You didn`t finish the task');

    // this.setState({
    //   label: '',
    //   min: '',
    //   sec: '',
    // });

  render() {
    return (
      <header className="app-header">
        <h1 className="app-title">todos</h1>
        <form className="new-todo-form" onKeyUp={this.onEnter}>
          <input
            className="new-todo"
            placeholder="What needs to be done?"
            onChange={(e) => this.setState({label: e.target.value})}
            value={this.state.label}
            autoFocus
          ></input>
          {/* Два новых ИНПУТА */}
          <input 
            className="new-todo-form__timer"
            placeholder="Min" 
            onChange={(e) => this.setState({min: e.target.value})}
            value={this.state.min}
            autoFocus
          ></input>
          <input 
            className="new-todo-form__timer"
            placeholder="Sec"
            onChange={(e) => this.setState({sec: e.target.value})}
            value={this.state.sec}
            autoFocus
          ></input>
          </form>
      </header>
    );
}
}
