import { formatDistanceToNow } from 'date-fns';
import { Component } from 'react';
// import './task.css';

export default class Task extends Component {
  constructor() {
    super()
    this.state = {
      label: '',
      edit: false,
      time: new Date(),
    }
  }


  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(), 1000
    )
    this.countTimer = setInterval( () => this.tick(), 1000)
  }

  tick() {
    this.setState({
      time: new Date(),
    })
  }

  countDownTimer() {
    if(this.props.timer > 0) this.props.timer -= 1
  }

  startTimer() {
    this.timerID = setInterval( () => this.countTimer(), 1000)
  }

  stopTimer() {
    clearInterval(this.timerID)
    clearInterval(this.countTimer)
  }

  // tick() {
  //   this.setState({ })
  // }

  onEdited() {
    this.setState({
      edit: true,
      label: this.props.label,
    });
  };

  changeLabel(event) {
    this.setState({
      label: event.target.value,
    });
  };

  onSubmitEdit(event) {
    event.preventDefault();
    const { updateEdit, id } = this.props;
    const text = this.state.label;
    updateEdit(id, text);
    this.setState({
      label: '',
      edit: false,
    });
  };

  render() {
    console.log('this.state: ', this.state)
    console.log(this.props.timer)
    const { label, done, id, date, timer, onDeleted, onCompleted } = this.props;
    const min = Math.floor(timer / 60)
    const sec = timer % 60
    console.log('min: ' + min, 'sec: ' + sec)

    let dateCreated = formatDistanceToNow(date, { includeSeconds: true, addSuffix: true });
    let liClass = '';
    if (done) {
      liClass += ' completed';
    }
    if (this.state.edit) liClass += ' editing';

    return (
      <li className={liClass}>
        <div className="view">
          <input
            id={id}
            className="toggle"
            type="checkbox"
            checked={done}
            onChange={(event) => {
              onCompleted(id, event.target.checked);
            }}
          ></input>
          <label htmlFor={id}>
            <span className="title">{label}</span>
            <span className="description">
                <button className="icon icon-play" onClick={this.startTimer}></button>
                <button className="icon icon-pause" onClick={this.stopTimer}></button>
                {` ${min}:${sec}`}
            </span>
            <span className="description">{dateCreated}</span>
          </label>
          <button className="icon icon-edit" onClick={this.onEdited}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
        <form onSubmit={this.onSubmitEdit}>
          <input
            className="edit"
            type="text"
            value={this.state.label}
            onChange={(event) => this.changeLabel(event)}
          ></input>
        </form>
      </li>
    );
  }
}
